--geyachao: 将AI搜敌优先级表转化为整数类型(优化效率)
for index, tabS in pairs(_tab_skill) do
	local AI_priorityList = tabS.AI_priorityList
	if AI_priorityList then
		--整数类型的搜敌优先级表
		local AI_priorityList_ex = {}
		
		--逐一转化
		for i = 1, #AI_priorityList, 1 do
			local strPriorityType = AI_priorityList[i] --优先级类型(字符串)
			local priorityType = hVar.AI_PRIORITY_STRING_MAP[strPriorityType]--优先级类型
			
			if priorityType then
				AI_priorityList_ex[#AI_priorityList_ex+1] = {priorityType,}
			elseif (#strPriorityType > 9) and (string.sub(strPriorityType, 1, 9) == "PRI_BUFF_") then --有某种buff的
				--buff的类型
				local buffType = string.sub(strPriorityType, 10, #strPriorityType)
				AI_priorityList_ex[#AI_priorityList_ex+1] = {hVar.AI_PRIORITY.PRI_BUFF, buffType,}
			elseif (#strPriorityType > 11) and (string.sub(strPriorityType, 1, 11) == "PRI_NOBUFF_") then --没有某种buff的
				--buff的类型
				local buffType = string.sub(strPriorityType, 12, #strPriorityType)
				AI_priorityList_ex[#AI_priorityList_ex+1] = {hVar.AI_PRIORITY.PRI_NOBUFF, buffType,}
			else
				--无效的字符串类型
				--...
			end
		end
		
		--存储
		tabS.AI_priorityList_ex = AI_priorityList_ex
	end
end

